'use client';

import { useEffect, useState } from 'react';
import { useSupabase } from '@/components/SupabaseProvider';
import { createClient } from '@/lib/supabase';
import Link from 'next/link';
import { useRouter } from 'next/navigation';
import { Plus, Clock, LogOut, Loader2, ArrowLeft, FolderPlus, Sparkles, Trash2 } from 'lucide-react';
import { motion, AnimatePresence } from 'framer-motion';
import { AnimatedFolder } from '@/components/ui/3d-folder';

interface Project {
    id: string;
    name: string;
    description: string | null;
    created_at: string;
    updated_at: string;
}

// Rotating gradient palette for folder colors
const folderGradients = [
    "linear-gradient(135deg, #10b981, #059669)",   // emerald
    "linear-gradient(135deg, #6366f1, #4f46e5)",   // indigo
    "linear-gradient(135deg, #f59e0b, #d97706)",   // amber
    "linear-gradient(135deg, #ec4899, #db2777)",   // pink
    "linear-gradient(135deg, #06b6d4, #0891b2)",   // cyan
    "linear-gradient(135deg, #a855f7, #9333ea)",   // purple
    "linear-gradient(135deg, #ef4444, #dc2626)",   // red
    "linear-gradient(135deg, #84cc16, #65a30d)",   // lime
];

export default function DashboardPage() {
    const { user, isLoading: authLoading } = useSupabase();
    const [projects, setProjects] = useState<Project[]>([]);
    const [isLoading, setIsLoading] = useState(true);
    const [deletingId, setDeletingId] = useState<string | null>(null);
    const router = useRouter();
    const supabase = createClient();

    useEffect(() => {
        if (authLoading) return;
        if (!user) {
            router.push('/login');
            return;
        }
        fetchProjects();
        // eslint-disable-next-line react-hooks/exhaustive-deps
    }, [user, authLoading, router]);

    const fetchProjects = async () => {
        if (!user) return;
        setIsLoading(true);
        try {
            const { data, error } = await supabase
                .from('projects')
                .select('*')
                .eq('user_id', user.id)
                .order('updated_at', { ascending: false });

            if (error) throw error;
            setProjects(data || []);
        } catch (err) {
            console.error('Failed to fetch projects:', err);
        } finally {
            setIsLoading(false);
        }
    };

    // Delete confirmation state
    const [projectToDelete, setProjectToDelete] = useState<Project | null>(null);

    const handleDeleteClick = (e: React.MouseEvent, project: Project) => {
        e.preventDefault();
        e.stopPropagation();
        setProjectToDelete(project);
    };

    const confirmDelete = async () => {
        if (!projectToDelete) return;

        const projectId = projectToDelete.id;
        setDeletingId(projectId);
        setProjectToDelete(null);

        try {
            const { error } = await supabase
                .from('projects')
                .delete()
                .eq('id', projectId);

            if (error) throw error;
            setProjects(projects.filter((p) => p.id !== projectId));
        } catch (err) {
            console.error('Failed to delete project:', err);
        } finally {
            setDeletingId(null);
        }
    };

    const handleSignOut = async () => {
        await supabase.auth.signOut();
        router.push('/');
        router.refresh();
    };

    const formatDate = (dateStr: string) => {
        const date = new Date(dateStr);
        const now = new Date();
        const diffMs = now.getTime() - date.getTime();
        const diffMins = Math.floor(diffMs / 60000);
        const diffHours = Math.floor(diffMs / 3600000);
        const diffDays = Math.floor(diffMs / 86400000);

        if (diffMins < 1) return 'Just now';
        if (diffMins < 60) return `${diffMins}m ago`;
        if (diffHours < 24) return `${diffHours}h ago`;
        if (diffDays < 7) return `${diffDays}d ago`;
        return date.toLocaleDateString('en-US', { month: 'short', day: 'numeric', year: 'numeric' });
    };

    const getUserName = () => {
        if (!user?.email) return 'there';
        const name = user.email.split('@')[0];
        return name.charAt(0).toUpperCase() + name.slice(1);
    };

    if (authLoading || isLoading) {
        return (
            <div className="min-h-screen flex items-center justify-center bg-zinc-950">
                <div className="flex flex-col items-center gap-4">
                    <div className="relative">
                        <div className="absolute inset-0 rounded-full bg-emerald-500/20 blur-xl animate-pulse" />
                        <Loader2 size={32} className="animate-spin text-emerald-400 relative z-10" />
                    </div>
                    <p className="text-zinc-500 text-sm animate-pulse">Loading your workspace...</p>
                </div>
            </div>
        );
    }

    return (
        <div className="min-h-screen relative bg-zinc-950 overflow-hidden">
            {/* Background ambient glow */}
            <div className="fixed top-0 right-0 w-[600px] h-[600px] bg-emerald-500/[0.03] rounded-full blur-[120px] pointer-events-none" />
            <div className="fixed bottom-0 left-0 w-[400px] h-[400px] bg-blue-500/[0.02] rounded-full blur-[100px] pointer-events-none" />

            {/* Header */}
            <header className="relative z-10 border-b border-zinc-800/50 bg-zinc-950/80 backdrop-blur-xl sticky top-0">
                <div className="max-w-6xl mx-auto px-6 py-4 flex items-center justify-between">
                    <div className="flex items-center gap-4">
                        <Link href="/" className="text-zinc-500 hover:text-white transition-colors">
                            <ArrowLeft size={18} />
                        </Link>
                        <div className="flex items-center gap-2.5">
                            <svg width="22" height="22" viewBox="0 0 24 24" fill="none" stroke="#10b981" strokeWidth="1.5">
                                <polygon points="12 2 22 8.5 22 15.5 12 22 2 15.5 2 8.5 12 2" />
                                <line x1="12" y1="22" x2="12" y2="15.5" />
                                <polyline points="22 8.5 12 15.5 2 8.5" />
                            </svg>
                            <span className="text-white font-semibold text-lg">Lucidz</span>
                        </div>
                    </div>

                    <div className="flex items-center gap-3">
                        <span className="text-zinc-600 text-sm hidden sm:inline">{user?.email}</span>
                        <div className="h-4 w-px bg-zinc-800 hidden sm:block" />
                        <button
                            onClick={handleSignOut}
                            className="flex items-center gap-1.5 px-3 py-1.5 rounded-lg text-zinc-500 hover:text-white hover:bg-white/5 transition-all text-sm"
                        >
                            <LogOut size={14} />
                            Sign Out
                        </button>
                    </div>
                </div>
            </header>

            {/* Main Content */}
            <main className="max-w-6xl mx-auto px-6 py-12 relative z-10">
                {/* Hero Header */}
                <motion.div
                    initial={{ opacity: 0, y: 20 }}
                    animate={{ opacity: 1, y: 0 }}
                    transition={{ duration: 0.6, ease: [0.22, 1, 0.36, 1] }}
                    className="mb-14"
                >
                    <div className="flex flex-col sm:flex-row sm:items-end justify-between gap-6">
                        <div>
                            <h1 className="text-4xl md:text-5xl font-bold text-transparent bg-clip-text bg-gradient-to-br from-white via-zinc-200 to-zinc-500 tracking-tight mb-3">
                                Welcome back, {getUserName()}.
                            </h1>
                            <p className="text-zinc-500 text-lg font-light">
                                {projects.length > 0
                                    ? `You have ${projects.length} project${projects.length > 1 ? 's' : ''} in your workspace.`
                                    : 'Ready to build something extraordinary?'}
                            </p>
                        </div>
                        <Link
                            href="/generate"
                            className="group flex items-center gap-2 px-6 py-3 rounded-xl text-sm font-semibold border border-emerald-500/30 text-emerald-400 bg-emerald-500/10 hover:bg-emerald-500/20 transition-all hover:scale-[1.02] active:scale-[0.98] shrink-0"
                        >
                            <Plus size={18} strokeWidth={2.5} />
                            New Project
                        </Link>
                    </div>
                </motion.div>

                {/* Empty State */}
                <AnimatePresence>
                    {projects.length === 0 && (
                        <motion.div
                            initial={{ opacity: 0, y: 20 }}
                            animate={{ opacity: 1, y: 0 }}
                            exit={{ opacity: 0 }}
                            className="flex flex-col items-center justify-center py-32 text-center"
                        >
                            <div className="relative mb-8">
                                <div className="absolute inset-0 rounded-full bg-emerald-500/10 blur-2xl scale-150" />
                                <div className="relative w-24 h-24 rounded-3xl bg-zinc-900 border border-zinc-800 flex items-center justify-center">
                                    <FolderPlus size={40} strokeWidth={1} className="text-emerald-400/60" />
                                </div>
                            </div>
                            <h2 className="text-2xl font-bold text-white mb-3 tracking-tight">Your canvas is empty</h2>
                            <p className="text-zinc-500 max-w-md mb-8 font-light">
                                Start by describing the application you want to build. Our 4-agent pipeline will architect, code, and deliver it in seconds.
                            </p>
                            <Link
                                href="/generate"
                                className="flex items-center gap-2 px-6 py-3 rounded-xl text-sm font-semibold bg-emerald-500 text-black hover:bg-emerald-400 transition-all shadow-lg shadow-emerald-500/20"
                            >
                                <Sparkles size={16} />
                                Generate Your First Project
                            </Link>
                        </motion.div>
                    )}
                </AnimatePresence>

                {/* Project Grid with 3D Folders */}
                {projects.length > 0 && (
                    <motion.div
                        initial={{ opacity: 0 }}
                        animate={{ opacity: 1 }}
                        transition={{ duration: 0.4, delay: 0.2 }}
                        className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-8"
                    >
                        {/* New Project Folder */}
                        <motion.div
                            initial={{ opacity: 0, y: 20 }}
                            animate={{ opacity: 1, y: 0 }}
                            transition={{ duration: 0.4 }}
                        >
                            <div
                                onClick={() => router.push('/generate')}
                                className="relative flex flex-col items-center justify-center p-6 rounded-2xl cursor-pointer bg-white/[0.02] border border-dashed border-zinc-800 hover:border-emerald-500/30 hover:bg-white/[0.04] transition-all duration-500 group"
                                style={{ minHeight: "280px" }}
                            >
                                <div className="absolute inset-0 bg-gradient-to-br from-emerald-500/[0.03] to-transparent opacity-0 group-hover:opacity-100 transition-opacity duration-500 rounded-2xl" />
                                <div className="relative z-10 w-16 h-16 rounded-2xl bg-zinc-800/50 border border-zinc-700/50 flex items-center justify-center group-hover:border-emerald-500/30 group-hover:bg-emerald-500/10 transition-all duration-300 mb-4">
                                    <Plus size={28} strokeWidth={1.5} className="text-zinc-500 group-hover:text-emerald-400 transition-colors duration-300" />
                                </div>
                                <span className="relative z-10 text-zinc-500 group-hover:text-emerald-400 text-sm font-semibold transition-colors duration-300">
                                    New Project
                                </span>
                            </div>
                        </motion.div>

                        {/* Project Folders */}
                        {projects.map((project, index) => (
                            <motion.div
                                key={project.id}
                                initial={{ opacity: 0, y: 20 }}
                                animate={{ opacity: 1, y: 0 }}
                                transition={{ duration: 0.4, delay: 0.05 * (index + 1) }}
                                className="relative group/folder"
                            >
                                <AnimatedFolder
                                    title={project.name}
                                    subtitle={`Last edited: ${formatDate(project.updated_at)}`}
                                    gradient={folderGradients[index % folderGradients.length]}
                                    onClick={() => router.push(`/project/${project.id}`)}
                                    projects={[
                                        { id: project.id, title: project.name, image: undefined },
                                    ]}
                                />

                                {/* Delete button ΓÇö sits on top of the folder */}
                                <button
                                    onClick={(e) => handleDeleteClick(e, project)}
                                    className="absolute top-3 right-3 z-40 p-2 rounded-xl opacity-0 group-hover/folder:opacity-100 text-zinc-600 hover:text-red-400 hover:bg-red-400/10 transition-all duration-200 backdrop-blur-sm"
                                    disabled={deletingId === project.id}
                                >
                                    {deletingId === project.id ? (
                                        <Loader2 size={14} className="animate-spin" />
                                    ) : (
                                        <Trash2 size={14} />
                                    )}
                                </button>
                            </motion.div>
                        ))}
                    </motion.div>
                )}
            </main>

            {/* Delete Confirmation Modal */}
            <AnimatePresence>
                {projectToDelete && (
                    <motion.div
                        initial={{ opacity: 0 }}
                        animate={{ opacity: 1 }}
                        exit={{ opacity: 0 }}
                        className="fixed inset-0 z-50 flex items-center justify-center p-4 bg-black/70 backdrop-blur-sm"
                        onClick={() => setProjectToDelete(null)}
                    >
                        <motion.div
                            initial={{ opacity: 0, scale: 0.95, y: 10 }}
                            animate={{ opacity: 1, scale: 1, y: 0 }}
                            exit={{ opacity: 0, scale: 0.95, y: 10 }}
                            transition={{ duration: 0.2 }}
                            className="w-full max-w-md bg-zinc-900 border border-zinc-800 rounded-2xl p-6 shadow-2xl"
                            onClick={(e) => e.stopPropagation()}
                        >
                            <h3 className="text-lg font-semibold text-white mb-2">Delete Project?</h3>
                            <p className="text-zinc-400 text-sm mb-6 leading-relaxed">
                                Are you sure you want to delete <span className="text-white font-medium">&ldquo;{projectToDelete.name}&rdquo;</span>?
                                This action cannot be undone and will delete all generated versions.
                            </p>

                            <div className="flex justify-end gap-3">
                                <button
                                    onClick={() => setProjectToDelete(null)}
                                    className="px-4 py-2 rounded-xl text-sm font-medium text-zinc-400 hover:text-white hover:bg-white/5 transition-colors"
                                >
                                    Cancel
                                </button>
                                <button
                                    onClick={confirmDelete}
                                    className="px-4 py-2.5 rounded-xl text-sm font-semibold bg-red-500/10 text-red-400 hover:bg-red-500 hover:text-white transition-all"
                                >
                                    Delete Project
                                </button>
                            </div>
                        </motion.div>
                    </motion.div>
                )}
            </AnimatePresence>
        </div>
    );
}

'use client';

import { useState, useEffect } from 'react';
import { useRouter } from 'next/navigation';
import ChatInterface from '@/components/ChatInterface';
import SmartInput, { AttachmentPayload } from '@/components/SmartInput';
import CodePreview from '@/components/CodePreview';
import DownloadBtn from '@/components/DownloadBtn';
import { ViewToggle } from '@/components/ui/view-toggle';
import { TextShimmerWave } from '@/components/ui/text-shimmer-wave';
import { SandpackFiles } from '@codesandbox/sandpack-react';
import { useSupabase } from '@/components/SupabaseProvider';
import { MessageSquare, Eye, History, Github, ChevronLeft } from 'lucide-react';
import { VersionHistorySidebar } from '@/components/VersionHistorySidebar';
import { uploadAsset } from '@/lib/storage';

type ViewMode = 'preview' | 'code';
type GenerationStage = 'idle' | 'planning' | 'architecting' | 'coding' | 'reviewing' | 'modifying' | 'complete';
type MobileTab = 'chat' | 'preview';

const stageMessages: Record<GenerationStage, string> = {
    idle: '',
    planning: 'Constructing a Master Plan....',
    architecting: 'Orchestrating things....',
    coding: 'Generating the code....',
    reviewing: 'Reviewing code quality....',
    modifying: 'Applying modifications....',
    complete: '',
};

export default function Home() {
    const [files, setFiles] = useState<SandpackFiles>({});
    const [isLoading, setIsLoading] = useState(false);
    const [plan, setPlan] = useState<string>('');
    const [architect, setArchitect] = useState<string>('');
    const [diagram, setDiagram] = useState<string>('');
    const [review, setReview] = useState<string>('');
    const [viewMode, setViewMode] = useState<ViewMode>('preview');
    const [sidebarWidth, setSidebarWidth] = useState(420);
    const [stage, setStage] = useState<GenerationStage>('idle');

    const [isFollowUpLoading, setIsFollowUpLoading] = useState(false);
    const [userPrompt, setUserPrompt] = useState<string>('');
    const [followUpMessages, setFollowUpMessages] = useState<Array<{ prompt: string; response: string }>>([]);
    const [isResizing, setIsResizing] = useState(false);
    const [projectId, setProjectId] = useState<string | null>(null);
    const [mobileTab, setMobileTab] = useState<MobileTab>('chat');
    const [isHistoryOpen, setIsHistoryOpen] = useState(false);
    const [currentVersionId, setCurrentVersionId] = useState<string | null>(null);
    const [isExporting, setIsExporting] = useState(false);
    const { user, supabase, isLoading: authLoading } = useSupabase();
    const router = useRouter();



    // Auth gate: redirect to login if not authenticated
    useEffect(() => {
        if (!authLoading && !user) {
            router.push('/login');
        }
    }, [user, authLoading, router]);

    // Robust resize handling
    useEffect(() => {
        if (!isResizing) return;

        const handleMouseMove = (e: MouseEvent) => {
            e.preventDefault();
            // Clamp width between 280 and 700
            const newWidth = Math.max(280, Math.min(700, e.clientX));
            setSidebarWidth(newWidth);
        };

        const handleMouseUp = (e: MouseEvent) => {
            e.preventDefault();
            setIsResizing(false);
        };

        // Also handle mouse leaving the window to prevent stuck state
        const handleMouseLeave = (e: MouseEvent) => {
            // If mouse leaves the document, end resize
            if (e.clientY <= 0 || e.clientX <= 0 ||
                e.clientX >= window.innerWidth || e.clientY >= window.innerHeight) {
                setIsResizing(false);
            }
        };

        // Apply resizing class to body for global cursor
        document.body.classList.add('resizing');
        document.body.style.cursor = 'col-resize';
        document.body.style.userSelect = 'none';

        // Add listeners
        document.addEventListener('mousemove', handleMouseMove, { passive: false });
        document.addEventListener('mouseup', handleMouseUp);
        document.addEventListener('mouseleave', handleMouseLeave);

        return () => {
            // Cleanup
            document.body.classList.remove('resizing');
            document.body.style.cursor = '';
            document.body.style.userSelect = '';
            document.removeEventListener('mousemove', handleMouseMove);
            document.removeEventListener('mouseup', handleMouseUp);
            document.removeEventListener('mouseleave', handleMouseLeave);
        };
    }, [isResizing]);

    const startResize = (e: React.MouseEvent) => {
        e.preventDefault();
        setIsResizing(true);
    };

    // Generate handler - uses streaming endpoint for real-time updates
    const handleGenerate = async (prompt: string, attachments: AttachmentPayload) => {
        setIsLoading(true);
        setPlan('');
        setArchitect('');
        setDiagram('');
        setFiles({});
        setStage('planning');
        setUserPrompt(prompt);
        setFollowUpMessages([]);

        // Log attachments for debugging
        console.log('Generating with attachments:', {
            images: attachments.images.length,
            pdf: attachments.pdf?.name
        });

        try {
            // Build attachment payload for backend
            // Send first image for now (backend can be extended for multiple)
            const primaryImage = attachments.images[0] || null;
            const attachment = primaryImage || attachments.pdf;

            // Upload asset if present (NEW PIPELINE)
            let imageAssetUrl = null;
            if (primaryImage) {
                console.log('Uploading asset to storage...');
                imageAssetUrl = await uploadAsset(primaryImage.data, primaryImage.name, primaryImage.mimeType);
                if (imageAssetUrl) console.log('Asset uploaded:', imageAssetUrl);
            }

            const response = await fetch('http://localhost:8000/generate-stream', {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({
                    prompt,
                    attachment: attachment ? {
                        name: attachment.name,
                        type: attachment.type,
                        data: attachment.data,
                        mimeType: attachment.mimeType
                    } : null,
                    // Also send all images for future multi-image support
                    images: attachments.images.map(img => ({
                        name: img.name,
                        type: img.type,
                        data: img.data,
                        mimeType: img.mimeType
                    })),
                    user_id: user?.id || null,
                    project_id: projectId,
                    image_asset_url: imageAssetUrl, // Pass persistent URL
                }),
            });

            if (!response.ok) {
                throw new Error('Failed to generate code');
            }

            // Read the SSE stream
            const reader = response.body?.getReader();
            const decoder = new TextDecoder();

            if (!reader) {
                throw new Error('No response body');
            }

            let buffer = '';

            while (true) {
                const { done, value } = await reader.read();

                if (done) break;

                buffer += decoder.decode(value, { stream: true });

                // Process complete SSE events (lines starting with "data: ")
                const lines = buffer.split('\n');
                buffer = lines.pop() || ''; // Keep incomplete line in buffer

                for (const line of lines) {
                    if (line.startsWith('data: ')) {
                        try {
                            const data = JSON.parse(line.slice(6));

                            // Handle different stages
                            switch (data.stage) {
                                case 'planning':
                                    setStage('planning');
                                    break;

                                case 'plan_complete':
                                    if (data.plan) {
                                        setPlan(data.plan);
                                    }
                                    break;

                                case 'architecting':
                                    setStage('architecting');
                                    break;

                                case 'architect_complete':
                                    if (data.architect) {
                                        setArchitect(data.architect);
                                    }
                                    if (data.diagram) {
                                        setDiagram(data.diagram);
                                    }
                                    break;

                                case 'coding':
                                    setStage('coding');
                                    break;

                                case 'coding_complete':
                                    if (data.files) {
                                        setFiles(data.files);
                                    }
                                    break;

                                case 'reviewing':
                                    setStage('reviewing');
                                    break;

                                case 'complete':
                                    if (data.files) {
                                        setFiles(data.files);
                                    }
                                    if (data.review) {
                                        setReview(data.review);
                                    }
                                    setStage('complete');
                                    break;

                                case 'error':
                                    console.error('Stream error:', data.message);
                                    setPlan(`Error: ${data.message}`);
                                    setStage('idle');
                                    break;

                                case 'saved':
                                    if (data.project_id) {
                                        setProjectId(data.project_id);
                                        // Update URL to include project ID without full navigation
                                        window.history.replaceState({}, '', `/project/${data.project_id}`);
                                    }
                                    break;
                            }
                        } catch {
                            console.warn('Failed to parse SSE data:', line);
                        }
                    }
                }
            }

        } catch (error) {
            console.error('Error:', error);
            setPlan('Error: Failed to generate code. Check the backend console.');
            setStage('idle');
        } finally {
            setIsLoading(false);
        }
    };



    // Revert handler
    const handleRevert = async (versionId: string) => {
        try {
            const response = await fetch(`http://localhost:8000/versions/${versionId}`);
            if (!response.ok) throw new Error('Failed to fetch version');
            const version = await response.json();

            if (version.code_snapshot) {
                setFiles(version.code_snapshot);
                setCurrentVersionId(versionId);
                // Reset stage to complete so user can follow up from here
                setStage('complete');
                setPlan(`Restored to version from ${new Date(version.created_at).toLocaleTimeString()}`);
            }
        } catch (error) {
            console.error('Revert error:', error);
        }
    };

    // GitHub Export Handler
    const handleExportToGithub = async () => {
        if (!user || !projectId) return;

        setIsExporting(true);
        try {
            // Get session to retrieve provider token
            const { data: { session } } = await supabase.auth.getSession();
            const githubToken = session?.provider_token;

            if (!githubToken) {
                // Redirect to GitHub login
                const { error } = await supabase.auth.signInWithOAuth({
                    provider: 'github',
                    options: {
                        scopes: 'repo',
                        redirectTo: window.location.href,
                    },
                });

                if (error) {
                    console.error('GitHub login error:', error);
                    alert('Failed to initiate GitHub login');
                }
                setIsExporting(false);
                return;
            }

            const response = await fetch('http://localhost:8000/api/export-to-github', {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({
                    project_id: projectId,
                    github_token: githubToken
                })
            });

            if (!response.ok) {
                const error = await response.json();
                throw new Error(error.detail || 'Export failed');
            }

            const data = await response.json();
            // Open new repo in new tab
            window.open(data.repo_url, '_blank');

        } catch (error) {
            console.error('GitHub Export Error:', error);
            alert(`Failed to export: ${error instanceof Error ? error.message : 'Unknown error'}`);
        } finally {
            setIsExporting(false);
        }
    };

    // Follow-up handler - sends modification request directly to coder agent
    const handleFollowUp = async (prompt: string, attachments: AttachmentPayload) => {
        const hasContent = prompt.trim() || attachments.images.length > 0 || attachments.pdf;
        if (!hasContent || Object.keys(files).length === 0) return;

        // Add user's message to the chat immediately
        setFollowUpMessages(prev => [...prev, { prompt: prompt.trim(), response: '' }]);
        setIsFollowUpLoading(true);
        setStage('modifying');

        try {
            // Build attachment for follow-up (use first image or PDF)
            const primaryImage = attachments.images[0] || null;
            const attachment = primaryImage || attachments.pdf;

            const response = await fetch('http://localhost:8000/followup-stream', {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({
                    prompt,
                    current_files: files,
                    review_feedback: review,
                    attachment: attachment ? {
                        name: attachment.name,
                        type: attachment.type,
                        data: attachment.data,
                        mimeType: attachment.mimeType
                    } : null,
                    user_id: user?.id || null,
                    project_id: projectId,
                }),
            });

            if (!response.ok) {
                throw new Error('Failed to apply modifications');
            }

            const reader = response.body?.getReader();
            const decoder = new TextDecoder();

            if (!reader) {
                throw new Error('No response body');
            }

            let buffer = '';

            while (true) {
                const { done, value } = await reader.read();

                if (done) break;

                buffer += decoder.decode(value, { stream: true });

                const lines = buffer.split('\n');
                buffer = lines.pop() || '';

                for (const line of lines) {
                    if (line.startsWith('data: ')) {
                        try {
                            const data = JSON.parse(line.slice(6));

                            switch (data.stage) {
                                case 'modifying':
                                    setStage('modifying');
                                    break;

                                case 'complete':
                                    if (data.files) {
                                        setFiles(data.files);
                                    }
                                    setStage('complete');
                                    // Add response to the last follow-up message
                                    setFollowUpMessages(prev => {
                                        const updated = [...prev];
                                        if (updated.length > 0) {
                                            updated[updated.length - 1].response = data.summary || 'Modifications applied successfully.';
                                        }
                                        return updated;
                                    });

                                    break;

                                case 'error':
                                    console.error('Follow-up error:', data.message);
                                    setStage('complete');
                                    break;
                            }
                        } catch {
                            console.warn('Failed to parse SSE data:', line);
                        }
                    }
                }
            }

        } catch (error) {
            console.error('Follow-up Error:', error);
            setStage('complete');
        } finally {
            setIsFollowUpLoading(false);
        }
    };

    // Get status text for header
    const getStatusText = () => {
        if (stage === 'complete' && Object.keys(files).length > 0) {
            return 'Generated Output';
        }
        if (isLoading) {
            return stageMessages[stage] ? 'Processing...' : 'Waiting for input...';
        }
        return 'Waiting for input...';
    };

    // Auto-switch to Preview tab on mobile when code generation completes
    useEffect(() => {
        if (stage === 'complete' && Object.keys(files).length > 0) {
            setMobileTab('preview');
        }
    }, [stage, files]);

    // Show loading while auth is being resolved
    if (authLoading) {
        return (
            <div className="flex h-screen w-full items-center justify-center" style={{ background: 'var(--lucid-bg)' }}>
                <div className="w-6 h-6 border-2 border-t-transparent rounded-full animate-spin"
                    style={{ borderColor: 'var(--lucid-green)', borderTopColor: 'transparent' }}
                />
            </div>
        );
    }

    return (
        <div className="relative w-full h-[100dvh] bg-[#050505] overflow-hidden">
            {/* ΓöÇΓöÇΓöÇ MOBILE LAYOUT (< 768px) ΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇ */}
            <div className="flex md:hidden flex-col h-full w-full relative">

                {/* Floating Back Button (Mobile Preview only) */}
                {mobileTab !== 'chat' && (
                    <button
                        onClick={() => router.push('/dashboard')}
                        className="absolute top-4 left-4 z-50 p-2 rounded-full bg-zinc-900/80 backdrop-blur-md border border-zinc-800 shadow-xl text-zinc-400 hover:text-white transition-colors"
                    >
                        <ChevronLeft size={20} />
                    </button>
                )}

                {/* Floating View Toggle */}
                <div className="absolute top-4 left-1/2 -translate-x-1/2 z-50 flex w-44 h-10 p-1 rounded-full bg-zinc-900/80 backdrop-blur-md border border-zinc-800 shadow-2xl">
                    <div className="flex items-center w-full relative">
                        {/* Sliding background pill */}
                        <div
                            className="absolute left-0 h-8 w-1/2 rounded-full transition-transform duration-300 ease-[cubic-bezier(0.16,1,0.3,1)]"
                            style={{
                                transform: mobileTab === 'chat' ? 'translateX(0%)' : 'translateX(100%)',
                                backgroundColor: '#22c55e',
                            }}
                        />

                        {/* Chat button */}
                        <button
                            onClick={() => setMobileTab('chat')}
                            className={`flex justify-center items-center z-10 w-1/2 h-8 rounded-full text-xs font-semibold transition-colors duration-300 ${mobileTab === 'chat' ? 'text-black' : 'text-zinc-400 hover:text-white'}`}
                        >
                            Chat
                        </button>

                        {/* Preview button */}
                        <button
                            onClick={() => setMobileTab('preview')}
                            className={`flex justify-center items-center z-10 w-1/2 h-8 rounded-full text-xs font-semibold transition-colors duration-300 ${mobileTab === 'preview' ? 'text-black' : 'text-zinc-400 hover:text-white'}`}
                        >
                            Preview
                        </button>
                    </div>
                </div>

                {/* Main Content Area filling available space above input */}
                {mobileTab !== 'chat' && (
                    <div className="flex-1 relative overflow-hidden bg-[#050505]">
                        <div className="absolute inset-x-0 bottom-0 top-[60px]">
                            <CodePreview files={files} mode="preview" />
                        </div>
